require 'twitter'
require 'dotenv/load'
require 'pry'

# V√©rification de l'acc√®s aux cl√©s d'API
puts "API Key: #{ENV['TWITTER_API_KEY']}"
puts "API Secret: #{ENV['TWITTER_API_SECRET']}"
puts "Access Token: #{ENV['TWITTER_ACCESS_TOKEN']}"
puts "Access Token Secret: #{ENV['TWITTER_ACCESS_TOKEN_SECRET']}"

class TwitterBot
  def initialize
    # Ajoutons un point d'arr√™t ici pour v√©rifier l'initialisation du client
    binding.pry

    @client = Twitter::REST::Client.new do |config|
      config.consumer_key        = ENV['TWITTER_API_KEY']
      config.consumer_secret     = ENV['TWITTER_API_SECRET']
      config.access_token        = ENV['TWITTER_ACCESS_TOKEN']
      config.access_token_secret = ENV['TWITTER_ACCESS_TOKEN_SECRET']
    end
  end

# M√©thode pour tester la connexion √† l'API Twitter
  def test_connection
    begin
      user = @client.user
      puts "Authentification r√©ussie. Bienvenue, #{user.screen_name}!"
    rescue Twitter::Error => e
      puts "Erreur d'authentification : #{e.message}"
    end
  end
  

# M√©thode pour envoyer un message direct √† un utilisateur par son nom d'utilisateur
  def send_direct_message(username, message)
    binding.pry
    @client.create_direct_message(username, message)
    puts "Message direct envoy√© √† @#{username} : #{message}"
  end

# M√©thode pour envoyer "Bonjour" √† 5 journalistes choisis al√©atoirement
  def greet_journalists(journalists)
    # S√©lectionner 5 journalistes au hasard parmi la liste
    binding.pry
    selected_journalists = journalists.sample(5)

    # Envoyer un message √† chaque journaliste s√©lectionn√©
    selected_journalists.each do |journalist|
      send_direct_message(journalist, "Bonjour ! J'esp√®re que tu passes une bonne journ√©e ! üåü")
    end
  end

  def promote_hashtag_to_journalists(journalists, message, hashtag)
    # Point d'arr√™t pour v√©rifier les param√®tres `journalists`, `message` et `hashtag`
    binding.pry

    journalists.each do |journalist|
      tweet_to_user(journalist, message, hashtag)
    end
  end
end

